import React, { Component } from 'react';
import CalcButton from './Components/CalcButton';
import ActionButton from './Components/ActionButton';
import './App.css';

class App extends Component {
  state = {
    value: '0'
  }
  render() {
    return (
      <div className="App">
        <div className='calc-wrapper'>
            <div className='output-screen'></div>
            <div className='row'>
              <CalcButton>7</CalcButton>
              <CalcButton>8</CalcButton>
              <CalcButton>9</CalcButton>
              <CalcButton>*</CalcButton>
            </div>
            <div className='row'>
              <CalcButton>4</CalcButton>
              <CalcButton>5</CalcButton>
              <CalcButton>6</CalcButton>
              <CalcButton>/</CalcButton>
            </div>
            <div className='row'>
              <CalcButton>1</CalcButton>
              <CalcButton>2</CalcButton>
              <CalcButton>3</CalcButton>
              <CalcButton>+</CalcButton>
            </div>
            <div className='row'>
              <CalcButton className='zero'>0</CalcButton>
              <CalcButton>.</CalcButton>
              <CalcButton>-</CalcButton>
            </div>
            <div className='row'>
              <ActionButton>AC</ActionButton>
              <ActionButton>=</ActionButton>
            </div>


        </div>
      </div>
    );
  }
}

export default App;
